package printhello;

public class T1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Common Datatypes and Variables name and Value.
		int a = 20;
		int b = 10;
		int c = 40;
		
		//Multiply the value
		int d = (a*b*c);
        System.out.println("The Multiply total value (a,b,c):"+d);
        
        //Adding the value
        int e = (a+b+c);
        System.out.println("The adding total value (a,b,c):"+e);
        
        //Subtract the value
        int f = (c-b-a);
        System.out.println("The Subtract total value (c,b,a):"+f);
        
        //Divide the value
        int g = (c/a);
        System.out.println("The Divide total value (c/a):"+g);
	}

}
