package printhello;

public class Test1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//For Loop condition
		for(int i=10;i<=100;i=i+10) {
			
			System.out.println(i);
		}
		System.out.println();
		
		//While Loop condition
		int i=10;
		
		while(i<=100) {
			
			System.out.println(i);
			i=i+10;
		}
		System.out.println();
		
		//Do while Loop condition
		int a=0;
		do {
			a=a+10;
			System.out.println(a);
		}while(a<=90);
			

	}

}
